import styled from "styled-components";

/**
 * Component to display for a fraction of a second
 * while the page is getting ready.
 */
const FlashScreen = styled.div`
  background-color: white;
  position: fixed;
  width: 100%;
  height: 100%;
`;

export default FlashScreen;
